import { Link, useMatch, useResolvedPath } from "react-router-dom";
const Navbar = () => {

    return (
        <nav className="nav">
            <Link
                to="/" className="site-title">Site Name
            </Link>
            <ul>
                <CustomLink to="/contact">Pricing</CustomLink>
                <CustomLink to="/about">About</CustomLink>
            </ul>

        </nav>
    );
}

